mapper.select:
SELECT * FROM
"USER" WHERE USER_ID= #{userId}
AND STATUS = 'Y'

SELECT COUNT(*)
FROM USER
WHERE USER_EMAIL = #{userEmail}

SELECT
USER_PWD
FROM "USER"
WHERE USER_ID = #{userId}
AND USER_EMAIL =
#{userEmail}

SELECT
COUNT(*)
FROM "USER"
WHERE USER_ID = #{userId}
AND USER_EMAIL =
#{userEmail}

SELECT WINE_NO, WINE_NAME, GRAPE_NO, COUNTRY_NO, VINTAGE, PRICE, VOLUME, QUANTITY, CONTENT, STATUS,
ORIGIN_NAME, CHANGE_NAME
FROM WISH LEFT JOIN WINE USING (WINE_NO) LEFT JOIN WINE_IMAGE USING (WINE_NO) WHERE USER_NO = #{userNo}

mapper.delete:
DELETE FROM WISH
WHERE USER_NO = #{userNo}
	AND WINE_NO = #{wineNo}

mapper.select:
SELECT USER_NO, GRADE_NO, USER_ID, USER_PWD, USER_NAME, USER_EMAIL, USER_SSN, PHONE, ADDRESS, ENROLL_DATE, MODIFY_DATE, STATUS
FROM "USER"
WHERE USER_ID = #{userId}

SELECT USER_NO, GRADE_NO, USER_ID, USER_PWD, USER_NAME, USER_EMAIL, USER_SSN, PHONE, ADDRESS, ENROLL_DATE, MODIFY_DATE, STATUS
FROM "USER"
WHERE USER_NO = #{userNo}
